#include <map>
#include <vector>
#include <algorithm>
#include <functional>

using namespace std;

class Solution {
private:
    map<int, int> createFreqMap(vector<int>& vec) {
        map<int, int> freqMap;
        for (int i = 0; i < vec.size(); i++) {
            freqMap[vec[i]]++;
        }

        return freqMap;
    }

    void decreaseEleFreq(map<int, int>& freqMap, int ele) {
        freqMap[ele]--;
        if (freqMap[ele] == 0) {
            freqMap.erase(ele);
        }
    }

    int calcNumContentChildren(vector<int>& g, vector<int>& s) {
        int numChildren = g.size();
        map<int, int> sFreqMap = createFreqMap(s);

        int numContentChildren = 0;
        for (int i = 0; i < numChildren; i++) {
            if (sFreqMap.empty()) {
                break;
            }
            int greed = g[i];

            auto it = sFreqMap.lower_bound(greed);
            if (it == sFreqMap.end()) {
                continue;
            }

            /**
             * pick smallest size cookie that can satisfy current child's greed
             * if such a cookie is found
             * - increase count of content children
             * - decrease frequency of that sized cookie in frequency map
             */
            if ((it->first == greed) || (it != sFreqMap.begin())) {
                numContentChildren++;
                decreaseEleFreq(sFreqMap, it->first);
            }
        }

        return numContentChildren;
    }

public:
    int findContentChildren(vector<int>& g, vector<int>& s) {
        if (s.empty()) {
            return 0;
        }

        sort(g.begin(), g.end());
        sort(s.begin(), s.end());

        int numCookies = s.size();
        if (s[numCookies - 1] < g[0]) {
            return 0;
        }

        return calcNumContentChildren(g, s);
    }
};
